@prefix bot:  <https://w3id.org/bot#> .
@prefix ex:   <https://example.org/bot#> .
@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> .

# tbox
ex:directAccess rdfs:subClassOf bot:Interface .

# abox
<Stairwell>  a bot:Space .
<Space_A>    a bot:Space .
<Space_B>    a bot:Space .
<Bouiling_A> a bot:Building .
<Level_1>    a bot:Storey .
<Level_2>    a bot:Storey .
<Level_3>    a bot:Storey .

# building / storeys relations
<Bouiling_A> bot:hasStorey <Level_1> , <Level_2> , <Level_3> .

# regular spaces
<Level_2> bot:hasSpace <Space_A> , <Space_B> .

# special case
<Bouiling_A> bot:hasSpace <Stairwell> .

##############
# BOT SIMPLIFIED
##############
bot:Zone 
	a owl:Class .
bot:Site 
	a owl:Class ;
	rdfs:subClassOf bot:Zone .
bot:Building 
	a owl:Class ;
	rdfs:subClassOf bot:Zone .
bot:Storey 
	a owl:Class ;
	rdfs:subClassOf	bot:Zone .
bot:Space 
	a owl:Class ;
	rdfs:subClassOf	bot:Zone .
bot:Element 
	a owl:Class .
bot:Interface 
	a owl:Class .

bot:containsZone 
	a owl:ObjectProperty , owl:TransitiveProperty ;
	rdfs:domain	bot:Zone ;
        rdfs:range      bot:Zone .
bot:adjacentZone 
	a owl:ObjectProperty , owl:SymmetricProperty ;
	rdfs:domain     bot:Zone ;
        rdfs:range      bot:Zone .
bot:hasBuilding 
	a owl:ObjectProperty ;
	rdfs:subPropertyOf bot:containsZone ;
	rdfs:domain     bot:Zone ;
        rdfs:range      bot:Building .				
bot:hasStorey 
	a owl:ObjectProperty ;
	rdfs:subPropertyOf bot:containsZone ;
	rdfs:domain     bot:Zone ;
        rdfs:range      bot:Storey .
bot:hasSpace 
	a owl:ObjectProperty ;
	rdfs:subPropertyOf bot:containsZone ;
	rdfs:domain     bot:Zone ;
        rdfs:range      bot:Space .	
bot:adjacentElement 
	a owl:ObjectProperty ;
	rdfs:domain     bot:Zone ;
        rdfs:range      bot:Element .
bot:containsElement 
	a owl:ObjectProperty ;
	rdfs:domain     bot:Zone ;
        rdfs:range      bot:Element ;
	owl:propertyChainAxiom  ( bot:containsZone bot:containsElement ) .
bot:hostsElement 
	a owl:ObjectProperty ;
	rdfs:domain     bot:Element ;
        rdfs:range      bot:Element .
bot:interfaceOf 
	a owl:ObjectProperty ;
	rdfs:domain     bot:Interface .

[] a owl:AllDisjointClasses ;
        owl:members
              ( 
		bot:Site
		bot:Element
                bot:Space
                bot:Storey
                bot:Building ) .
[] a owl:AllDisjointClasses ;
        owl:members
              ( 
		bot:Zone
		bot:Element ) .
bot:Interface rdfs:subClassOf [
    a owl:Restriction ;
    owl:onProperty bot:interfaceOf ;
    owl:cardinality 2
] .

